void MessViewBase::sliderValueChangedCallbackHandler(const touchgfx::Slider& src, int value)
{
    if (&src == &slider_helligkeit)
    {
        //Display_Helligkeit
        //When slider_helligkeit value changed call virtual function
        //Call Adj_Displ_PWM
        Adj_Displ_PWM(value);
    }
}

void MessViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &But_HV_I_P)
    {
        //HVI_Plus
        //When But_HV_I_P clicked call virtual function
        //Call HVI_Plus
        HVI_Plus();
    }
    if (&src == &But_HV_I_M)
    {
        //HVI_Minus
        //When But_HV_I_M clicked call virtual function
        //Call HVI_Minus
        HVI_Minus();
    }
}
